mapper.update:
UPDATE hhs_menu
SET icon = #{icon}, color=#{color}
WHERE menu_id=#{menu_id}

UPDATE hhs_menu
SET quick_id = NULL, quick_menu_order = NULL
WHERE quick_id IS NOT NULL OR quick_menu_order IS NOT NULL;

UPDATE hhs_menu
SET quick_id = #{quick_id}, quick_menu_order = #{quick_menu_order}
WHERE menu_id = #{menu_id}
